mapper.delete:
delete from comment
where id in

    #{commentId}

mapper.select:
select c.id, ui.avatar, ui.nickname, replyUserI.nickname replyNickname, a.title, c.comment_content, c.create_time, c.is_review, c.type from ( select id, user_id, reply_user_id ruid, topic_id, comment_content, create_time, is_review, type from comment
is_delete = #{commentsVO.isDelete}
and type = #{commentsVO.type}
and is_review = #{commentsVO.review}
order by create_time desc limit #{commentsVO.pageNum},#{commentsVO.pageSize} ) c left join user_auth ua on ua.id = c.user_id left join user_info ui on ua.user_info_id = ui.id left join user_auth replyUserA on replyUserA.id = c.ruid left join user_info replyUserI on replyUserA.user_info_id = replyUserI.id left join article a on a.id = c.topic_id
ui.nickname like concat('%',#{commentsVO.keywords},'%')

select count(c.id)
from comment c
left join user_auth ua on ua.id = c.user_id
left join user_info ui on ua.user_info_id = ui.id

    c.is_delete = #{commentsVO.isDelete}

        and c.type = #{commentsVO.type}


        and c.is_review = #{commentsVO.review}


        and ui.nickname like concat('%',#{commentsVO.keywords},'%')

select * from ( select c.id, c.parent_id, c.user_id, ui.nickname, ui.avatar, c.reply_user_id, replyUi.nickname replyNickname, c.comment_content, c.create_time, row_number() over (PARTITION BY c.parent_id order by c.create_time) row_num from comment c left join user_auth ua on c.user_id = ua.id left join user_info ui on ua.user_info_id = ui.id left join user_auth replyUa on c.reply_user_id = replyUa.id left join user_info replyUi on replyUa.user_info_id = replyUi.id where c.is_review = 1 and c.parent_id in
#{commentId}
) com where 3 > com.row_num;

select parent_id commentId, count(*) replyCount from comment where is_review = 1 and parent_id in
#{commentId}
group by parent_id

SELECT topic_id as id, COUNT( 1 ) AS comment_count FROM comment WHERE topic_id IN
#{topicId}
AND parent_id IS NULL GROUP BY topic_id
