mapper.update:
update user_info ui
set ui.is_delete = #{userInfo.isDelete}
where ui.id = #{userInfo.id}

mapper.select:
select ua.id userId, avatar, nickname, r.role_name roles, (select count(a.id) from article a where a.user_id=ua.id) blogCount, ui.is_delete isDelete, ua.create_time, ua.login_time from ( select id, avatar, nickname, is_delete from user_info limit #{pageNum},#{pageSize} ) ui left join user_auth ua on ui.id = ua.user_info_id left join in_user_role iur on iur.user_id=ui.id left join role r on iur.role_id = r.id
ui.nickname like concat('%',#{keywords},'%')
;
